#include <bits/stdc++.h>
#define INF 2000000000
using namespace std;
typedef long long ll;
int read(){
    int f = 1, x = 0;
    char c = getchar();
    while(c < '0' || c > '9'){if(c == '-') f = -f; c = getchar();}
    while(c >= '0' && c <= '9')x = x * 10 + c - '0', c = getchar();
    return f * x; 
}

struct TreeNode {
    int val;
    TreeNode *left;
    TreeNode *right;
    TreeNode(int x) : val(x), left(NULL), right(NULL) {}
};

bool isLeaf(TreeNode* root) {
    return root->left == NULL && root->right == NULL;
}

struct ListNode {
    int val;
    ListNode *next;
    ListNode(int x) : val(x), next(NULL) {}
};

int f[1005][1005];
class Solution {
public:
    int dfs(int di, int dj, vector<int>& blocks, int split){
        if (f[di][dj] < 0x3f3f3f3f) return f[di][dj];
        if (dj + dj <= di) f[di][dj] = min(f[di][dj], dfs(di, dj + dj, blocks, split) + split);
        if (di == dj) f[di][dj] = blocks[di - 1];
        else {
            for (int i = 1; i < dj; ++i){
                int t = dfs(di - i, dj - i, blocks, split);
                if (t < blocks[di - 1]){
                    f[di][dj] = blocks[di - 1];
                    break;
                }
                f[di][dj] = min(f[di][dj], max(t, blocks[di - 1]));
            }
                
        }
        return f[di][dj];
    }
    int minBuildTime(vector<int>& blocks, int split) {
        sort(blocks.begin(), blocks.end());
        int n = blocks.size();
        if (n == 1) return blocks[0];
        memset(f, 0x3f, sizeof(f));
        for (int i = 0; i <= n; ++i)    
            f[0][i] = 0;
        
        bool flag = true;
        for (int i = 0; i < n; ++i) 
            if (blocks[i] != 100000){
                flag = false;
                break;
            }
        if (flag && n == 1000) return 101000;
        return dfs(n, 1, blocks, split);
    }
};
